SetVar(isStartProcessing, 0)
If(#notific_id# > 0){
    DBFind("@1notifications").Where({"ecosystem": "#ecosystem_id#", "id": "#notific_id#"}).Columns("page_params->wallet_id,date_start_processing,notification->type,processing_info->account,closed").Vars(note)
    If(#note_date_start_processing# > 0){
        SetVar(isStartProcessing, 1)
    }
}

SetVar(this_page, ts_consortium_member_contract)
Div(content-wrapper){
    Form(){
        DBFind("@1ts_wallets").Where({"id": "#note_page_params_wallet_id#", "rejected_at": 0}).Vars(wallet)
        If(#wallet_id# > 0){
            Div(list-group-item){
                Div(text-center text-bold h4 m0){
                    LangRes(@1investor)
                }
            }
            Div(list-group-item){
                Div(row){
                    Div(col-sm-3 text-right text-bold){
                        $@1investor$
                    }
                    Div(col-sm-9 text-left){
                        LinkPage(Class: text-primary h5 text-bold, Page: @1profile_view, PageParams: "v_account=#wallet_buyer_account#"){
                            #wallet_buyer_account#
                        }
                    }
                }
                Div(row){
                    Div(col-sm-3 text-right text-bold){
                        $@1signed_at$
                    }
                    Div(col-sm-9 text-left){
                        DateTime(#wallet_signed_at#, "YYYY-MM-DD HH:MI:SS")
                    }
                }
                Div(row mt-sm){
                    Div(col-sm-3 text-right text-bold){
                        $@1pub_key$
                    }
                    Div(col-sm-9 text-left){
                        Span(Class: h6, Body: #wallet_pub_buyer#)
                    }
                }
                Div(row){
                    Div(col-sm-3 text-right text-bold){
                        $@1ts_wallet_btc$
                    }
                    Div(col-sm-9 text-left){
                        Span(Class: h6, Body: #wallet_wallet_btc#)
                    }
                }
                Div(row){
                    Div(col-sm-3 text-right text-bold){
                        $@1ts_wallet_eth$
                    }
                    Div(col-sm-9 text-left){
                        Span(Class: h6, Body: #wallet_wallet_eth#)
                    }
                }
            }
            If(#note_closed# == 0){
                Div(list-group-item){
                    If(And(#note_processing_info_account# == #account_id#, #isStartProcessing# == 1)){
                        Button(Body: $@1reject$, Class: btn btn-default, Page: #this_page#, PageParams: "notific_id=#notific_id#", Contract: TsConsortiumMemberAction, Params: "Action=reject,WalletId=#note_page_params_wallet_id#,NotificId=#notific_id#")
                        Button(Body: $@1accept$, Class: btn btn-primary pull-right, Page: #this_page#, PageParams: "notific_id=#notific_id#", Contract: TsConsortiumMemberAction, Params: "Action=accept,WalletId=#note_page_params_wallet_id#,NotificId=#notific_id#")
                    }.ElseIf(#isStartProcessing# == 1){
                        Div(text-muted text-center){
                            $@1notice_processing_started_another_member$
                        }
                    }.ElseIf(#isStartProcessing# == 0){
                        Div(text-right){
                            Button(Body: $@1processing$, Class: btn btn-primary, Page: #this_page#, PageParams: "notific_id=#notific_id#", Contract: @1NotificationsProcess, Params: "NotificId=#notific_id#")
                        }
                    }
                }
            }
        }.Else{
            Div(list-group-item text-center h4){
                $@1wallet$ $@1not_found$
            }
        }
    }
}

Form(){
    Div(row){
        Div(col-md-2 mt-lg text-right){
            Label(){
                LangRes(@1ts_wallet_btc)
            }
        }
        Div(col-md-10){
            Input(Name: WalletBtc, Type: textarea).Validate(minLength:27, maxLength:34)
            InputErr(Name: WalletBtc, minLength: $@1validate_wallet_btc$, maxLength: $@1validate_wallet_btc$)
        }
    }
    Div(row mt){
        Div(col-md-2 mt-lg text-right){
            Label(){
                LangRes(@1ts_wallet_eth)
            }
        }
        Div(col-md-10){
            Input(Name: WalletEth, Type: textarea).Validate(minLength:40, maxLength:44)
            InputErr(Name: WalletEth, minLength: $@1validate_wallet_eth$, maxLength: $@1validate_wallet_eth$)
        }
    }
    Button(Body: LangRes(@1add), Class: btn btn-primary pull-right mt, Page: @1ts_free_wallets, Contract: @1TsFreeWalletAdd)
    Button(Body: LangRes(@1back), Class: btn btn-default pull-right mt, Page: @1ts_free_wallets)
}

SetVar(this_page, @1ts_free_wallets).(this_table, @1ts_wallets)
Include(@1pager_header)

SetTitle("$@1ts_free_wallets$")
DBFind("@1applications").Where({"ecosystem": "#ecosystem_id#", "name": "Token sale"}).Vars(app)
SetVar(admin_role_id, AppParam(App: #app_id#, Name: "ts_role_sale_admin"))
If(And(#admin_role_id# > 0, #role_id# == #admin_role_id#)){
    AddToolButton(Title:$@1ts_free_wallet_add$, Page: @1ts_free_wallet_add, Icon: icon-wallet).Popup(Header: $@1ts_free_wallet_add$, Width: "50")
}

If(#search#){
    SetVar(where, {"$and": [{"deleted":0}, {"buyer_account":0}, {"$or":[{"wallet_btc": {"$ilike": "#search#"}}, {"wallet_eth": {"$ilike": "#search#"}}]}]})
}.Else{
    SetVar(where, {"$and": [{"deleted":0}, {"buyer_account":0}]})
}

Div(list-group-item ml-lg mr-lg pt-lg){
    SetVar(search_name, LangRes(@1wallet))
    Include(@1search)
}

DBFind(#this_table#, src).Where(#where#).Order({"id": "-1"}).Limit(#pager_limit#).Offset(#pager_offset#).Custom(_btc){
    Span(#wallet_btc#)
}.Custom(_eth){
    Span(#wallet_eth#)
}.Custom(action){
    Div(pull-right){
        If(#deleted# == 0){
            Button(Body: Em(Class: fa fa-trash), Class: btn btn-default, Contract: @1TsFreeWalletDelete, Params: "WalletId=#id#", Page: @1ts_free_wallets).Alert(Text: "$@1sure_want_delete$", ConfirmButton: $@1yes$, CancelButton: $@1no$, Icon: question)
        }
    }
}.Count(count)

Div(fullscreen){
    Div(table-responsive ml-lg mr-lg){
        Div(list-group-item){
            If(#count# > 0){
                Table(src, "$@1id$=id,$@1ts_wallet_btc$=_btc,$@1ts_wallet_eth$=_eth,=action")
            }.Else{
                Div(Class: text-center h4 text-muted, Body: "$@1ts_free_wallets$ $@1not_founded$")
            }
        }.Style(
            margin-top:-15px;
            tbody > tr:nth-of-type(odd) {
                background-color: #f8f9fc;
            }
        )
    }
}
Div(mt-sm ml-lg mr-sm mb-sm){
    Include(@1pager)
}

Form(){
