                    paramsNew["Id"] = Int(param["id"])
                    paramsNew["Value"] = Str(value)
                    paramsNew["Conditions"] = param["conditions"]
                    CallContract("@1EditAppParam", paramsNew)
                } else {
                    paramsNew["ApplicationId"] = $app_id
                    paramsNew["Name"] = name
                    paramsNew["Value"] = Str(value)
                    paramsNew["Conditions"] = "ContractConditions(\"AdminCondition\")"
                    CallContract("@1NewAppParam", paramsNew)
                }
            } else {
                warning LangRes("@1app_not_found", "en")
            }
        }
    }

    action {
        $app_id = DBFind("@1applications").Where({"ecosystem": $ecosystem_id, "name": "Token sale"}).One("id")
        updateParam("ts_role_sale_admin", $RoleAdmin)
        updateParam("ts_role_sale_manager", $RoleManager)
        updateParam("ts_role_sale_seller", $RoleSeller)
        updateParam("ts_role_snapswap", $RoleSnapswap)
    }
}

contract TsTransferAdmin {
    data {
        WalletStr string
        CurrencyType int
        AmountApl money
        TransferredDate string
        TransferredTime string
    }

    func getRoleId(name string) int {
        var rid int
        rid = Int(AppParam(Int($app_id), name, $ecosystem_id))
        if rid == 0 {
            warning Sprintf(LangRes("@1role_not_set_application_parameter", "en"), name)
        }
        return rid
    }

    func dateAddTime(d, t string) string {
        var dt string
        if Contains(d, "T00:00:00Z") {
            d = Replace(d, "T00:00:00Z", "")
        }
        if Size(t) == 5 {
            dt = Sprintf("%v %v:00", d, t)
        }
        return dt
    }

    conditions {
        $WAITING_MANAGER = 1
        $WAITING_SELLER = 2
        $FINISHED = 3
        $REJECTED = 4
        if $AmountApl <= 0 {
            warning "Invalid amount of APLA"
        }

        $transferredAt = UnixDateTime(dateAddTime($TransferredDate, $TransferredTime))
        if $transferredAt == 0 {
            warning "Invalid transfer datetime"
        }

        $app_id = Int(DBFind("@1applications").Where({"ecosystem": $ecosystem_id, "name": "Token sale"}).One("id"))
        if $app_id == 0 {
            warning LangRes("@1app_not_found", "en")
        }

        $currentRole = getRoleId("ts_role_sale_admin")
        if !RoleAccess($currentRole) {
            warning "This action is allowed only for the role id #" + $currentRole
        }

        if $CurrencyType <= 0 || $CurrencyType > 3 {
            warning "Invalid Currency Type"
        }

        if $CurrencyType == 1 {
            $wallet = DBFind("ts_wallets").Where({"wallet_btc": $WalletStr}).Row()
        }
        if $CurrencyType == 2 {
            $wallet = DBFind("ts_wallets").Where({"wallet_eth": $WalletStr}).Row()
        }
        if $CurrencyType == 3 {
            if $WalletStr == "" {
                warning LangRes("@1public_key_empty", "en")
            }

            $WalletStr = Replace($WalletStr, " ", "")
            if Size($WalletStr) < 128 {
                warning LangRes("@1key_too_short", "en")
            }
            if Size($WalletStr) > 130 {
                warning LangRes("@1key_too_long", "en")
            }

            var buyer_key int buyer_account string
            buyer_key = PubToID($WalletStr)

            if buyer_key == 0 {
                warning LangRes("@1public_key_invalid", "en")
            }

            buyer_account = IdToAddress(buyer_key)
            $wallet = DBFind("ts_wallets").Where({"buyer_account": buyer_account}).Row()
        }

        if !$wallet {
            warning "Buyer wallet is not found"
        }
    }

    action {
        var sale map saleId int
        sale["buyer_account"] = $wallet["buyer_account"]
        sale["currency_type"] = $CurrencyType
        sale["amount_apl"] = $AmountApl
        sale["sale_apl_admin"] = $account_id
        sale["created_at"] = $time
        sale["buyer_transferred_at"] = $transferredAt
        sale["status"] = $WAITING_MANAGER

        saleId = DBInsert("ts_sales", sale)

        // role Apla sales manager notification
        var params map title page string
        title = Sprintf("Check sale id %v", saleId)
        page = "ts_transfer_manager"
        params["sale_id"] = saleId
        $roleAplManager = getRoleId("ts_role_sale_manager")
        @1NotificationsSend("Rid,Sender,Header,Page,Params,Closure,CurrentRid", $roleAplManager, 2, title, page, params, 1, $currentRole)
    }
}

contract TsTransferManager {
    data {
        SaleId int
        NotificId int
    }

    func getRoleId(name string) int {
        var rid int
        rid = Int(AppParam(Int($app_id), name, $ecosystem_id))
        if rid == 0 {
            warning Sprintf(LangRes("@1role_not_set_application_parameter", "en"), name)
        }
        return rid
    }

    conditions {
        $WAITING_MANAGER = 1
        $WAITING_SELLER = 2
        $FINISHED = 3
        $REJECTED = 4
        $app_id = Int(DBFind("@1applications").Where({"ecosystem": $ecosystem_id, "name": "Token sale"}).One("id"))
        if $app_id == 0 {
            warning LangRes("@1app_not_found", "en")
        }
        $roleAplSeller = getRoleId("ts_role_sale_seller")
        $currentRole = getRoleId("ts_role_sale_manager")
        if !RoleAccess($currentRole) {
            warning "this action allowed only for role id #" + $currentRole
        }

        $sale = DBFind("ts_sales").Where({"id": $SaleId}).Row()
        if !$sale {
            warning "Sale is not found"
        }
    }

    action {
        @1NotificationsClose("NotificId", $NotificId)
        DBUpdate("ts_sales", Int($sale["id"]), {"sale_apl_manager": $account_id, "status": $WAITING_SELLER})

        // role Apla seller notification
        var params map title page string
        title = Sprintf("Transfer sale id %v", $sale["id"])
        params["sale_id"] = $sale["id"]
        page = "ts_transfer_seller"
        @1NotificationsSend("Rid,Sender,Header,Page,Params,Closure,CurrentRid", $roleAplSeller, 2, title, page, params, 1, $currentRole)

    }
}

contract TsTransferReject {
    data {
        Comment string
        RoleParam string
        NotificId int
    }

    func getRoleId(name string) int {
        var rid int
        rid = Int(AppParam(Int($app_id), name, $ecosystem_id))
        if rid == 0 {
            warning Sprintf(LangRes("@1role_not_set_application_parameter", "en"), name)
        }
        return rid
    }

    conditions {
        $WAITING_MANAGER = 1
        $WAITING_SELLER = 2
        $FINISHED = 3
        $REJECTED = 4

        if !($RoleParam == "ts_role_sale_manager" || $RoleParam == "ts_role_sale_seller") {
            warning "Invalid role parameter"
        }
        $app_id = Int(DBFind("@1applications").Where({"ecosystem": $ecosystem_id, "name": "Token sale"}).One("id"))
        var role noteSaleId int note map
        role = getRoleId($RoleParam)
        if !RoleAccess(role) {
            warning "This action is allowed only for the role id #" + role
        }
        note = DBFind("@1notifications").Where({"id": $NotificId}).Columns("page_params->sale_id,date_start_processing,notification->type").Row()
        if !note {
            warning "Notification is not found"
        }
        $needProcessing = 0
        if note["date_start_processing"] == 0 {
            $needProcessing = 1
        }
        noteSaleId = Int(note["page_params.sale_id"])
        $sale = DBFind("ts_sales").Where({"id": noteSaleId}).Row()
        if !$sale {
            warning "Sale is not found"
        }
        $saleId = Int($sale["id"])
    }

    action {
        if $needProcessing == 1 {
            @1NotificationsProcess("NotificId", $NotificId)
        }
        @1NotificationsClose("NotificId", $NotificId)
        var sale params map title body string prevAccount string
        title = Sprintf("Sale %v rejected", $saleId)
        body = $Comment
        params["sale_id"] = $saleId
        sale["status"] = $REJECTED
        if $RoleParam == "ts_role_sale_manager" {
            sale["sale_apl_manager"] = $account_id
            sale["manager_reject_comment"] = $Comment
            sale["manager_rejected_at"] = $time
            prevAccount = $sale["sale_apl_admin"]

        } elif $RoleParam == "ts_role_sale_seller" {
            sale["seller_account"] = $account_id
            sale["seller_reject_comment"] = $Comment
            sale["seller_rejected_at"] = $time
            prevAccount = $sale["sale_apl_manager"]
        }

        DBUpdate("ts_sales", $saleId, sale)
        @1NotificationsSend("MemberAccount,Sender,Header,Body,Page,Params,Closure", prevAccount, 1, title, body, "ts_notification", params, 1)

    }
}

