contract CreateCompany {
    data {
        company_name string 
        description string
        logo file
        country string
        city string
        organization string
    }
    conditions {
        $app_id = DBFind("@1applications").Where({ecosystem:$ecosystem_id, name:"FootballManager", deleted:0}).One("id")
        if !$app_id {
            warning LangRes("@1app_not_found", "en")
        }
    }
    action {
        $up_image = @1UploadFile("Name,Data,ApplicationId", $company_name, $logo, Int($app_id))
        var company_info map
        company_info["description"] = $description
        company_info["logo"] = $up_image
        company_info["city"] =  $city
        company_info["country"] = $country
        
        DBInsert("companies",{"info":company_info,"name":$company_name,"type":$organization,"ecosystem_id":$ecosystem_id})
    }
}

contract FootballRoleAssignment {
    data {
        role_name string
        company_id int
        notific_id int "optional"
        accept int "optional"
        info_update int "optional"
    }
    conditions {
        $admin_id = EcosysParam("role_admin")
        if $notific_id > 0 {
            $request = DBFind("@1notifications").Where({ecosystem:$ecosystem_id, id:$notific_id}).Columns("id,closed,sender->member_id,page_params->role_name,page_params->company_id,ecosystem").Row()
            if Int($request["closed"]) == 1 {
                warning LangRes("@1request_processed_already", "en")
            }
            if !RoleAccess(Int($admin_id)) {
                warning LangRes("@1access_denied", "en")
            } 
        } else{
            $check_role = DBFind("@1roles_participants").Where({"role->name":$role_name,"member->member_id":$key_id,"deleted":0,"ecosystem":$ecosystem_id})
            if $check_role {
                info("You are already assigned to this role.")
            }
            $check_request = DBFind("@1notifications").Where({"sender->member_id":$key_id,"page_params->role":$role_name,"closed":0,"ecosystem":$ecosystem_id})
            if $check_request {
                info("Request has already been sent")
            }
            $check_other_request = DBFind("@1notifications").Where({"page_params->role":$role_name,"closed":0,"page_params->accepted":"-1","ecosystem":$ecosystem_id}).One("id")
            if Int($check_other_request) > 0 {
                info("A request has already been sent to this role by other contragent")
            }
            if $role_name == "Club Representative" {
                $check_membership = DBFind("@1members").Where({"manager_info->club":$company_id,"manager_info->type":"Club","ecosystem":1}).One("id")
                if Int($check_membership) != 0 {
                    info("This club already has a contragent")
                }
            } elif $role_name == "Player Agency Representative" {
                $check_membership = DBFind("@1members").Where({"manager_info->agency":$company_id,"manager_info->type":"Agency","ecosystem":1}).One("id")
                if Int($check_membership) != 0 {
                    info("This company already has a contragent")
                }
            }
        }
    }
    action {   
        var req_params map
        req_params["role"] = $role_name
        req_params["company_id"] = $company_id
        if $notific_id > 0 {
            if $accept == 1 {
                req_params["accepted"] = 1
                req_params["type"] = "decision"
                $id_role_req = DBFind("@1roles").Where({"role_name":$role_name,"deleted":0,"ecosystem":$ecosystem_id}).One("id")
                @1RolesAssign("rid,member_id",$id_role_req,Int($request["sender.member_id"]))
            } else {
                req_params["accepted"] = 0
                req_params["type"] = "decision"
            }
            @1NotificationsSend("current_role_id,member_id,sender,icon_name,text_header,text_body,page_name,params_map,closure_type,eco_id",Int($admin_id),Int($request["sender.member_id"]), 2,"icon icon-envelope-open","Request accepted",LangRes("@1details_view", "en"),"role_request_view",req_params,1,$ecosystem_id)
            @1NotificationsProcess("notific_id", $notific_id)
            @1NotificationsClose("notific_id", $notific_id)
        } else {
            req_params["accepted"] = -1
            @1NotificationsSend("rid,sender,icon_name,text_header,text_body,page_name,params_map,closure_type,eco_id",Int($admin_id), 1, "icon icon-envelope-open", "New role request", LangRes("@1details_view", "en"), "role_request_view",req_params,1,$ecosystem_id)
        }
    } 
}

contract PlayerContractSign {
    data {
        contract_block string
        id_contract int
        role_id int 
    }
    conditions {
        $check_member = DBFind("@1roles_participants").Columns("id,role->id,role->name,member->member_id,deleted,ecosystem").Where({"role->id":$role_id,"member->member_id":$key_id,"deleted":0,"ecosystem":$ecosystem_id}).Row()
        
        if Int($check_member["id"]) == 0 {
            warning LangRes("@1role_not_found", "en")
        }
        
        if Str($check_member["role.name"]) == "Player" {
            $id_player = DBFind("player_contracts").Where({"id_player":$key_id})
            if !$id_player {
                warning LangRes("@1access_denied", "en")
            }
        } elif Str($check_member["role.name"]) == "Club Representative" {
            $id_contragent_club = DBFind("player_contracts").Where({"id_contragent_club":$key_id})
            if !$id_contragent_club {
                warning LangRes("@1access_denied", "en")
            }
        } elif Str($check_member["role.name"]) == "Player Agency Representative" {
            $id_contragent_agency = DBFind("player_contracts").Where({"id_contragent_agency":$key_id})
            if !$id_contragent_agency {
                warning LangRes("@1access_denied", "en")
            }
        } else {
            warning LangRes("@1access_denied", "en")
        }
        
            
    }
    action {
        $pub = DBFind("@1keys").Columns("id,pub,ecosystem").Where({"id":$key_id,"ecosystem":$ecosystem_id}).One("pub")
        $pub_sign = Sha256($pub)
        if $contract_block == "general" {
            $check_accept = DBFind("player_contracts").Columns("id,general_sign->accept").Where({"id":$id_contract}).Row()
            $plus_accept = Int($check_accept["general_sign.accept"]) + 1
            
            if Str($check_member["role.name"]) == "Club Representative" {
                DBUpdate("player_contracts",$id_contract,{"general_sign->time_contragent_club":$block_time,"general_sign->contragent_club":$pub_sign,"general_sign->accept":$plus_accept})
            }
            if Str($check_member["role.name"]) == "Player" {
                DBUpdate("player_contracts",$id_contract,{"general_sign->time_player":$block_time,"general_sign->player":$pub_sign,"general_sign->accept":$plus_accept})
            }
            if Str($check_member["role.name"]) == "Player Agency Representative" {
                DBUpdate("player_contracts",$id_contract,{"general_sign->time_contragent_agency":$block_time,"general_sign->contragent_agency":$pub_sign,"general_sign->accept":$plus_accept})
            }
        }
        if $contract_block == "salary" {
            $check_accept = DBFind("player_contracts").Columns("id,salary_sign->accept").Where({"id":$id_contract}).Row()
            $plus_accept = Int($check_accept["salary_sign.accept"]) + 1
            
            if Str($check_member["role.name"]) == "Club Representative" {
                DBUpdate("player_contracts",$id_contract,{"salary_sign->time_contragent_club":$block_time,"salary_sign->contragent_club":$pub_sign,"salary_sign->accept":$plus_accept})
            }
            if Str($check_member["role.name"]) == "Player" {
                DBUpdate("player_contracts",$id_contract,{"salary_sign->time_player":$block_time,"salary_sign->player":$pub_sign,"salary_sign->accept":$plus_accept})
            }
            if Str($check_member["role.name"]) == "Player Agency Representative" {
                DBUpdate("player_contracts",$id_contract,{"salary_sign->time_contragent_agency":$block_time,"salary_sign->contragent_agency":$pub_sign,"salary_sign->accept":$plus_accept})
            }
        }
        if $contract_block == "bonus" {
            $check_accept = DBFind("player_contracts").Columns("id,performance_bonus_sign->accept").Where({"id":$id_contract}).Row()
            $plus_accept = Int($check_accept["performance_bonus_sign.accept"]) + 1
            
            if Str($check_member["role.name"]) == "Club Representative" {
                DBUpdate("player_contracts",$id_contract,{"performance_bonus_sign->time_contragent_club":$block_time,"performance_bonus_sign->contragent_club":$pub_sign,"performance_bonus_sign->accept":$plus_accept})
            }
            if Str($check_member["role.name"]) == "Player" {
