        $check_accept = DBFind("player_contracts").Columns("id,id_player,id_club,general_sign->accept,salary_sign->accept,performance_bonus_sign->accept,other_advantages_sign->accept,insurance_sign->accept,other_bonus_sign->accept,agent_fees_sign->accept").Where({"id":$id_contract}).Row()
        if Int($check_accept["general_sign.accept"]) == 3 && Int($check_accept["salary_sign.accept"]) == 3 && Int($check_accept["performance_bonus_sign.accept"]) == 3 && Int($check_accept["other_advantages_sign.accept"]) == 3 && Int($check_accept["insurance_sign.accept"]) == 3 && Int($check_accept["other_bonus_sign.accept"]) == 3 && Int($check_accept["agent_fees_sign.accept"]) == 3 {
            $club_id = Int($check_accept["id_club"])
            DBUpdate("@1members",Int($check_accept["id_player"]),{"manager_info->club":$club_id})
            DBUpdate("player_contracts",$id_contract,{"closed":1})
        }    
    }
}

contract UpdateManagerInfo {
    data {
        company_id int
        notific_id int
    }
    conditions {
        $check_type = DBFind("companies").Columns("id,type").Where({"id":$company_id}).Row()
        $request = DBFind("@1notifications").Where({ecosystem:$ecosystem_id, id:$notific_id}).Columns("id,closed,sender->member_id,page_params->role,page_params->company_id,page_params->accepted,ecosystem").Row()
       
        if Int($request["closed"]) == 1 {
            warning LangRes("@1request_processed_already", "en")
        }
        if Int($request["page_params.accepted"]) != 1{
            warning LangRes("@1access_denied", "en")
        }
    }
    action {
        var m map
        //if $check_type["type"] == "Player Agency" {
        if $request["page_params.role"] == "Player Agency Representative" {
            m["agency"] = $company_id
            m["type"] = "Agency"
            DBUpdate("@1members", $key_id, {"manager_info":m})
        }
        //if $check_type["type"] == "Player" {
        if $request["page_params.role"] == "Player" {
            m["club"] = "0"
            m["agency"] = $company_id
            m["type"] = "Player"
            DBUpdate("@1members", $key_id, {"manager_info":m})
        }
        //if $check_type["type"] == "Club" {
        if $request["page_params.role"] == "Club Representative" {
            m["club"] = $company_id
            m["type"] = "Club"
            DBUpdate("@1members", $key_id, {"manager_info":m})
        }
        @1NotificationsClose("notific_id", $notific_id)
    }
}
