contract LrDepartChecks {
    data {
        ObjectId int
        NotificId int "optional"
        LegalStatus int "optional"
        EncumbranceCertificate int "optional"
        TaxReceiptAndBills int "optional"
        GovernmentPrice money "optional"
        MarketPrice money "optional"
        RoleId int
    }

    conditions {
        $app_id = DBFind("@1applications").Where({"ecosystem": $ecosystem_id, "name": "Land registry"}).One("id")
        $depart2_role = Int(AppParam(Int($app_id), "lr_depart2_role", $ecosystem_id))
        $depart3_role = Int(AppParam(Int($app_id), "lr_depart3_role", $ecosystem_id))

        if $app_id > 0 {
            if $depart2_role == 0 || $depart3_role == 0 || AppParam(Int($app_id), "lr_admin_role", $ecosystem_id) == 0 || AppParam(Int($app_id), "lr_bank_role", $ecosystem_id) == 0 {
                info LangRes("lr_all_roles_needed", "en")
            }
        } else {
            warning LangRes("@1app_not_found", "en")
        }
    }
    
    action {
        if $RoleId == $depart2_role {
            var object status m map
            object = DBFind("land_registry").WhereId($ObjectId).Row()
            if object["status"] {
                var depart3_status map
                depart3_status = JSONDecode(object["status"])
                status["tax_receipt_and_bills"] = depart3_status["tax_receipt_and_bills"]
            }

            status["legal_status"] = $LegalStatus
            status["encumbrance_certificate"] = $EncumbranceCertificate
            m["modification_date"] = $block_time
            m["status"] = status
            DBUpdate("land_registry", $ObjectId, m)
        }
        if $RoleId == $depart3_role {
            var object status m map
            object = DBFind("land_registry").WhereId($ObjectId).Row()
            if object["status"] {
                var depart2_status map
                depart2_status = JSONDecode(object["status"])
                status["legal_status"] = depart2_status["legal_status"]
                status["encumbrance_certificate"] = depart2_status["encumbrance_certificate"]
            }

            status["tax_receipt_and_bills"] = $TaxReceiptAndBills
            m["government_price"] = $GovernmentPrice
            m["market_price"] = $MarketPrice
            m["modification_date"] = $block_time
            m["status"] = status
            DBUpdate("land_registry", $ObjectId, m)
        }

        if $NotificId > 0 {
            var opened int
            opened = DBFind("@1notifications").Where({"id": $NotificId, "closed": 0, "ecosystem": $ecosystem_id}).One("id")
            if opened {
                @1NotificationsProcess("notific_id", $NotificId)
                @1NotificationsClose("notific_id", $NotificId)
            }
        }
    }
}

contract LrDepartRequest {
    data {
        ObjectId int
        DepartNumber int
    }

    conditions {
        $app_id = DBFind("@1applications").Where({ecosystem: $ecosystem_id, name: "Land registry"}).One("id")
        var requests_flag int
        if $DepartNumber == 2 {
            requests_flag = DBFind("@1notifications").Where({"ecosystem": $ecosystem_id, "page_params->object_id": $ObjectId, "page_params->depart2_requests": 2}).One("id")
            if requests_flag {
                warning LangRes("lr_requests_limit_reached", "en")
            }
        } elif $DepartNumber == 3 {
            requests_flag = DBFind("@1notifications").Where({"ecosystem": $ecosystem_id, "page_params->object_id": $ObjectId, "page_params->depart3_requests": 2}).One("id")
            if requests_flag {
                warning LangRes("lr_requests_limit_reached", "en")
            }
        }
    }
    
    action {
        var request params map
        request["sender"] = 1
        request["page_name"] = "lr_view"
        request["text_header"] = "$lr_additional_request$"
        request["text_body"] = "$lr_need_processing$"
        request["icon_name"] = "fa fa-bell-o"
        request["closure_type"] = 1
        request["popup"] = "true"
        params["object_id"] = $ObjectId
        
        if $DepartNumber == 2 {
            request["rid"] = Int(AppParam(Int($app_id), "lr_depart2_role", $ecosystem_id))
            params["depart2_requests"] = 2
            request["params_map"] = params
        } elif $DepartNumber == 3 {
            request["rid"] = Int(AppParam(Int($app_id), "lr_depart3_role", $ecosystem_id))
            params["depart3_requests"] = 2
            request["params_map"] = params
        }

        CallContract("@1NotificationsSend", request)
    }
}

contract LrEdit {
    data {
        ObjectId int
        LandUse int "optional"
        LandType int "optional"
        LandAuthority int "optional"
        BuildingsUseClass int "optional"
        RegistrationDate string "optional"
        Price money "optional"
        DeleteObject int "optional"
        RoleId int
    }

    func log(property, value string) {
        if value != $object[property] {
            var history map
            history["object_id"] = $ObjectId
            history["attribute_name"] = property
            history["old_value"] = $object[property]
            history["new_value"] = value
            history["member_id"] = $key_id
            history["date"] = $block_time
            DBInsert("land_registry_history", history)
        }
    }

    conditions {
        $app_id = DBFind("@1applications").Where({ecosystem: $ecosystem_id, name: "Land registry"}).One("id")
        $object = DBFind("land_registry").WhereId($ObjectId).Row()
        $admin_role = Int(AppParam(Int($app_id), "lr_admin_role", $ecosystem_id))

        if $app_id > 0 {
            if $admin_role == 0 || AppParam(Int($app_id), "lr_bank_role", $ecosystem_id) == 0 || AppParam(Int($app_id), "lr_depart2_role", $ecosystem_id) == 0 || AppParam(Int($app_id), "lr_depart3_role", $ecosystem_id) == 0 {
                info LangRes("lr_all_roles_needed", "en")
            }
        } else {
            warning LangRes("@1app_not_found", "en")
        }
    }
    
    action {
        if $DeleteObject != 1 {
            if $RoleId == $admin_role {
                var old_specifications specifications m map unixRegDate int
                unixRegDate = UnixDateTime(Sprintf("%s %s", $RegistrationDate, "00:00:00"))
                old_specifications = JSONDecode($object["specifications"])

                specifications["address"] = old_specifications["address"]
                specifications["area"] = old_specifications["area"]
                specifications["map_coords"] = old_specifications["map_coords"]
                specifications["land_use"] = $LandUse
                specifications["land_type"] = $LandType
                specifications["land_authority"] = $LandAuthority
                specifications["buildings_class"] = $BuildingsUseClass

                m["registration_date"] = unixRegDate
                m["specifications"] = specifications
                DBUpdate("land_registry", $ObjectId, m)

                log("land_use", Str($LandUse))
                log("land_type", Str($LandType))
                log("land_authority", Str($LandAuthority))
                log("buildings_class", Str($BuildingsUseClass))
                log("registration_date", Str($RegistrationDate))
            }
            if $object["owner_id"] == $key_id {
                DBUpdate("land_registry", $ObjectId, {"price": $Price})
                log("price", Str($Price))
            }
        } else {
            if $object["owner_id"] == $key_id || $RoleId == $admin_role {
                DBUpdate("land_registry", $ObjectId, {"deleted": 1})
                log("deleted", Str($DeleteObject))
            }
        }
    }
}

contract LrPurchaseSteps {
    data {
        ObjectId int
        Step int
    }

    conditions {
        $app_id = DBFind("@1applications").Where({"ecosystem": $ecosystem_id, "name": "Land registry"}).One("id")
        $object = DBFind("land_registry").WhereId($ObjectId).Row()

        var bank_role int
        bank_role = Int(AppParam(Int($app_id), "lr_bank_role", $ecosystem_id))
        $bank_id = DBFind("@1roles_participants").Where({"role->id": bank_role, "deleted": 0, "ecosystem": $ecosystem_id}).One("member->member_id")
        if !$bank_id {
            warning LangRes("lr_bank_account_not_found", "en")
        }

    }

    action {
        if $Step == -1 {
            var ownership_id int
            ownership_id = Int(DBFind("land_registry_ownership").Where({"object_id": $ObjectId}).One("id"))
            DBUpdate("land_registry_ownership", ownership_id, {"new_owner_id": $key_id, "step": 0})
        }
    }
}

contract LrSettings {
    data {
        RoleAdmin int
        RoleBank int
        RoleDepart2 int
        RoleDepart3 int
    }

    conditions{
        AdminCondition()
    }

    func updateParam(name string, id int) {
        if id > 0 {
            var param paramsNew map app_id int
            app_id = DBFind("@1applications").Where({ecosystem: $ecosystem_id, name: "Land registry"}).One("id")
            if app_id > 0 {
                param = DBFind("@1app_params").Where({ecosystem: $ecosystem_id, app_id: app_id, name: name}).Row()
                paramsNew["Value"] = Str(id)
                if param {
                    paramsNew["Id"] = Int(param["id"])
                    paramsNew["Conditions"] = param["conditions"]
                    CallContract("@1EditAppParam", paramsNew)
                } else {
                    paramsNew["App"] = Str(app_id)
                    paramsNew["Name"] = name
                    paramsNew["Conditions"] = "ContractConditions(\"AdminCondition\")"
                    CallContract("@1NewAppParam", paramsNew)
                }
            } else {
                warning LangRes("@1app_not_found", "en")
            }
        }
    }

    action {
        updateParam("lr_admin_role", $RoleAdmin)
        updateParam("lr_bank_role", $RoleBank)
        updateParam("lr_depart2_role", $RoleDepart2)
        updateParam("lr_depart3_role", $RoleDepart3)
    }
