contract ErrandCreate {
    data {
        errand_type int
        errand_name string
        errand_text string
        send_role_id int
        rec_role_id int "optional"
        member_id string "optional"
        end_date string
        end_time string
    }

    func isCalledFromPage() bool {
        return $stack[0] == Sprintf("@%v%v", $ecosystem_id, $this_contract)
    }
    func trimZeroTime(s string) string {
        if Contains(s, "T00:00:00Z") {
            s = s Replace(s, "T00:00:00Z", "")
        }
        return s
    }
    func dateAddTime(d, t string) string {
        var dt string
        if Size(t) == 5 {
            dt = Sprintf("%v %v:00", trimZeroTime(d), t)
        }
        return dt
    }
    func fixDatetimes() {
        $date_ended = dateAddTime($end_date, $end_time)

        if UnixDateTime($date_ended) == 0 { // invalid datetimes
            if isCalledFromPage(){
                var errs array
                if Size($end_date) < 10 {
                    warning LangRes("@1ending_date_invalid", "en")
                }
                if Size($end_time) < 5 {
                    warning LangRes("@1invalid_time_end", "en")
                }
            }
        }
        if UnixDateTime($date_ended) < $block_time {
            warning LangRes("@1ending_date_invalid", "en")
        }
    }

    conditions {
        $check_access = EcosysParam("errand_access")

        if $check_access {
            var rids array
            rids = JSONDecode("["+$check_access+"]")
            if !DBFind("@1roles_participants").Where({ecosystem:$ecosystem_id, "role->id":{"$in":rids}, "member->member_id":$key_id, deleted:0}).Row() {
                warning LangRes("@1access_denied", "en")
            }
        } else {
            var admin_id int
            admin_id = EcosysParam("role_admin")
        
            if !RoleAccess(Int(admin_id)) {
                warning LangRes("@1access_denied", "en")
            }
        }
        if $end_date == "" {
            info LangRes("@1ending_date_invalid", "en")
        }
        if $end_time == "" {
            info LangRes("@1invalid_time_end", "en")
        }
        if $errand_name == "" {
            info LangRes("@1empty_name", "en")
        }
        if $errand_text == "" {
            info LangRes("@1empty_text", "en")
        }
        if $errand_type == 0 {
            $valid_role = DBFind("roles").Where({ecosystem:$ecosystem_id, id:$rec_role_id, deleted:0}).One("id")
            if $valid_role == 0 {
                info LangRes("@1role_not_found", "en")
            }
        }
        if $errand_type == 1 {
            if $member_id == "" {
                info LangRes("@1empty_wallet", "en")
            }
            $member_id = AddressToId($member_id)
            $check_member = DBFind("@1keys").Where({id:$member_id, ecosystem:$ecosystem_id}).One("id")
            if Int($check_member) == 0 {
                warning LangRes("@1member_not_fount", "en")
            }
        }
        fixDatetimes()
    }

    action {
        var info_errand map
        info_errand["name"] = $errand_name
        info_errand["text"] = $errand_text
        info_errand["date_end"] = $date_ended
        info_errand["status"] = 0
        info_errand["type"] = "task"
        if $errand_type == 0 {
            info_errand["recipient_type"] = 0
        }
        if $errand_type == 1 {
            info_errand["recipient_type"] = 1
        }

        if $errand_type == 0 {
            @1NotificationsSend("current_role_id,rid,sender,icon_name,text_header,text_body,page_name,params_map,closure_type,eco_id", $send_role_id, $rec_role_id, 2, "icon icon-envelope-open", "New Errand", LangRes("@1details_view", "en"), "@1errand_view", info_errand, 1, $ecosystem_id)
        }
        if $errand_type == 1 {
            @1NotificationsSend("current_role_id,member_id,sender,icon_name,text_header,text_body,page_name,params_map,rid,closure_type,eco_id", $send_role_id, Int($member_id), 2, "icon icon-envelope-open", "New Errand", LangRes("@1details_view", "en"), "@1errand_view", info_errand, 0, 0,$ecosystem_id)
        }
    }
}

contract ErrandDecision {
    data {
        errand_id int
        errand_decision int
        answer_recipient string
    }

    conditions {
        if $answer_recipient == "" {
            info LangRes("@1empty_answer", "en")
        }

        $errand_map = DBFind("@1notifications").Where({"id":$errand_id}).Columns("id,processing_info,processing_info->member_id,page_params,page_params->name,page_params->text,page_params->type,page_params->date_end,closed,recipient->role_id,recipient->member_id,page_params->recipient_type,sender->role_id").Row()

        if Int($errand_map["page_params.recipient_type"]) == 0 {
            // role errand
            if !RoleAccess(Int($errand_map["recipient.role_id"])) {
                warning LangRes("@1access_denied", "en")
            }
            if $errand_map["processing_info"] == "" {
                info LangRes("@1start_process_errand", "en")
            }
            if Int($errand_map["processing_info.member_id"]) != $key_id {
                info LangRes("@1another_start_process_errand", "en")
            }
        } else {
            // member errand
            if Int($errand_map["recipient.member_id"]) != $key_id {
                warning LangRes("@1access_denied", "en")
            }
        }
        if $errand_map["closed"] != 0 {
            info LangRes("@1already_start_process_errand", "en")
        }
    }

    action {
        var answer_map map
        answer_map["name"] = $errand_map["page_params.name"]
        answer_map["text"] = $errand_map["page_params.text"]
        answer_map["type"] = $errand_map["page_params.type"]
        answer_map["date_end"] = $errand_map["page_params.date_end"]
        answer_map["answer"] = $answer_recipient

        if $errand_decision == 1 {
            answer_map["status"] = 1
        } else {
            answer_map["status"] = -1
        }

        var page_params_edit map
        page_params_edit["page_params"] = answer_map
        @1NotificationsUpdateParams("page_params,notific_id", page_params_edit, $errand_id)

        var info_errand map
        info_errand["decision_errand_id"] = $errand_id
        info_errand["type"] = "task_decision"

        if Int($errand_map["page_params.recipient_type"]) == 0 {
            // role errand
            @1NotificationsSend("current_role_id,rid,sender,icon_name,text_header,text_body,page_name,params_map,closure_type,eco_id", Int($errand_map["recipient.role_id"]), Int($errand_map["sender.role_id"]), 2, "icon icon-envelope-open", "Decision by errand", LangRes("@1details_view", "en"), "@1errand_view", info_errand, 1, $ecosystem_id)
        } else {
            // member errand
            @1NotificationsSend("rid,sender,icon_name,text_header,text_body,page_name,params_map,closure_type,eco_id", Int($errand_map["sender.role_id"]), 1, "icon icon-envelope-open", "Decision by errand", LangRes("@1details_view", "en"), "@1errand_view", info_errand, 1, $ecosystem_id)
        }

        @1NotificationsClose("notific_id", $errand_id) 
    }
}

contract ErrandStatusUpdate {
    data {}

    conditions {
        var admin_id int
        admin_id = EcosysParam("role_admin")

        if !RoleAccess(Int(admin_id)) {
            warning LangRes("@1access_denied", "en")
        }
    }

    action {
        var bt string
        bt = BlockTime()

        var errands array i lenErrands int v page_params_edit map
        errands = DBFind("@1notifications").Columns("id,page_params->date_end,page_params->type,page_params->status").Where({"page_params->type":"task", "page_params->date_end":{"$lte":bt}, "page_params->status":0})

        lenErrands = Len(errands)
        page_params_edit["page_params->status"] = -1
        while i < lenErrands {
            v = errands[i]
            @1NotificationsUpdateParams("page_params,notific_id", page_params_edit, Int(v["id"]))
            i = i + 1
        }
    }
}
