contract ExCancel {
    data {
        symbol string
    }
    conditions {
        ContractConditions("@1AdminCondition") 
    }
    action {
        var ids array
        var i ecosystem_hub wallet_apla wallet_hub int
        ecosystem_hub = Int(DBFind("@1symbol").Where({"name":$symbol}).Columns("ecosystem_hub").One("ecosystem_hub"))
        wallet_hub = Int(DBFind("@1symbol").Where({"name":$symbol}).Columns("wallet_hub").One("wallet_hub"))
        wallet_apla = Int(DBFind("@1symbol").Where({"name":$symbol}).Columns("wallet_apla").One("wallet_apla"))
        i = 0
        ids = DBFind("@1order_log").Where({"symbol":$symbol, "status":"A"}).Columns("id,client,client_value,counterparty_value,flag")
        while i < Len(ids) {
            var param map
            param = ids[i]
            var client id int
            client = Int(param["client"])
            id = Int(param["id"])
            var amount money
            if param["flag"] == "S" {
                amount = Money(param["counterparty_value"])
                @1TokensTransfer("Amount,SenderId,RecipientId,Comment,Ecosystem", amount, wallet_hub, client, "ExCancel", ecosystem_hub)
            }
            if param["flag"] == "B" {
                amount = Money(param["client_value"])
                @1TokensTransfer("Amount,SenderId,RecipientId,Comment,Ecosystem", amount, wallet_apla, client, "ExCancel", 1)
            }
            DBUpdate("@1order_log", id, {status:"C", lastmodified:$time})
            i = i + 1
        }  

    }
}

contract ExMatching {
    data {
        id int
        flag string
        symbol string
        client int
        quantity int
    }
    func checkBalance (client int, ecosystem int, value money) {
        var balance money
        balance = Money(DBFind("@1keys").Where({"id":client, "ecosystem":ecosystem}).Columns("amount").One("amount"))
        if value > balance {
            warning LangRes("@1ex_error_balance_check", "en")
        }
    }
    conditions {
        var tradestatus int
        tradestatus = Int(DBFind("@1symbol").Where({"name":$symbol}).Columns("tradeable").One("tradeable"))
        if (tradestatus != 1) {
           warning LangRes("@1ex_error_tradestatus", "en")
        }
        var check_eco_id ecosystem_hub int
        ecosystem_hub = Int(DBFind("@1symbol").Where({"name":$symbol}).Columns("ecosystem_hub").One("ecosystem_hub"))
        check_eco_id = Int(DBFind("@1keys").Where({"id":$client, "ecosystem":ecosystem_hub}).One("id"))
        if check_eco_id != $client {
            warning LangRes("@1ex_error_account", "en")
        }
        if $flag == "S" {
            var face_value money
            face_value = Money(DBFind("@1order_log").Where({"id":$id}).Columns("counterparty_value").One("counterparty_value"))
            checkBalance($client, ecosystem_hub, face_value) //check ecosystem_hub
        }
        if $flag == "B" {
            var apla_value money
            apla_value = Money(DBFind("@1order_log").Where({"id":$id}).Columns("client_value").One("client_value"))
            checkBalance($client, 1, apla_value)      
        }
        var counterparty check_cross int
        counterparty = Int(DBFind("@1order_log").Where({"id":$id}).Columns("client").One("client"))
        check_cross = Int(DBFind("@1symbol").Where({"name":$symbol}).One("cross"))
        if check_cross = 0 && $client == counterparty {
            warning LangRes("@1ex_error_cross_trades", "en")
        }
    }
    action {
        DBUpdate("@1order_log", $id, {status:"F", counterparty: $client, lastmodified:$time})
        var ecosystem_hub counterparty wallet_hub wallet_apla int
        counterparty = Int(DBFind("@1order_log").Where({"id":$id}).Columns("client").One("client"))
        ecosystem_hub = Int(DBFind("@1symbol").Where({"name":$symbol}).Columns("ecosystem_hub").One("ecosystem_hub"))
        wallet_hub = Int(DBFind("@1symbol").Where({"name":$symbol}).Columns("wallet_hub").One("wallet_hub"))
        wallet_apla = Int(DBFind("@1symbol").Where({"name":$symbol}).Columns("wallet_apla").One("wallet_apla"))
        var apla_value face_value price money
        price = Money(DBFind("@1order_log").Where({"id":$id}).Columns("price").One("price"))
        apla_value = Money(DBFind("@1order_log").Where({"id":$id}).Columns("client_value").One("client_value"))
        face_value = Money(DBFind("@1order_log").Where({"id":$id}).Columns("counterparty_value").One("counterparty_value")) 
        if $flag == "S" {
            @1TokensTransfer("Amount,SenderId,RecipientId,Comment,Ecosystem", face_value, $client, counterparty, "EXMatching", ecosystem_hub)
            @1TokensTransfer("Amount,SenderId,RecipientId,Comment,Ecosystem", apla_value, wallet_apla, $client, "EXMatching", 1)
        }
        if $flag == "B" {
            @1TokensTransfer("Amount,SenderId,RecipientId,Comment,Ecosystem", apla_value, $client, counterparty, "EXMatching", 1)
            @1TokensTransfer("Amount,SenderId,RecipientId,Comment,Ecosystem", face_value, wallet_hub, $client, "EXMatching", ecosystem_hub)          
        }
        //DBInsert("@1order_log", {flag: $flag, client: $client, counterparty: counterparty, venue: "EX", symbol: $symbol, quantity: $quantity, price: price, client_value: apla_value, counterparty_value: face_value, status: "F", lastmodified: $time})
    }
}

contract ExSymbol {
    data {
        id int "optional"
        status string "optional"
        operation string
        name string
        pricetick money
        range money
        lotsize money
        ecosystem int
        isTradeable int "optional"
        isCross int "optional"
        isOTC int "optional"
    }
    func exWallet (ecosystem int) int {
        var check_ex_wallet is_ex_wallet new_ex_wallet int
        check_ex_wallet = Int(DBFind("@1keys").Where({"id":$ecosystem}).Columns("id").One("id"))
        if check_ex_wallet == $ecosystem {
            is_ex_wallet = Int(DBFind("@1symbol").Where({"wallet_hub":check_ex_wallet}).Columns("wallet_hub").One("wallet_hub"))
            if check_ex_wallet == is_ex_wallet {
                return is_ex_wallet
            } else {
                new_ex_wallet = Int($ecosystem - ($ecosystem * 2))
                DBInsert("@1keys", {"id":new_ex_wallet, "ecosystem":$ecosystem})
                return new_ex_wallet
            }
        }
        if check_ex_wallet == 0 {
            DBInsert("@1keys", {"id":$ecosystem, "ecosystem":$ecosystem})
            return $ecosystem
        }
    }
    conditions {
        if $operation == "N" {
            if DBFind("@1keys").Where({"id":1, "ecosystem": 1}).Columns("id").One("id") {
                warning LangRes("@1ex_error_apla_wallet", "en")
            }
        }
        if $operation == "C" {
            var check_symbol string
            check_symbol = DBFind("@1symbol").Where({"name":$name}).Columns("name").One("name")
            if (check_symbol == $name) {
                warning LangRes("@1ex_error_symbol_name", "en")
            }
        }
        if ($ecosystem == 1) {
            warning LangRes("@1ex_error_mirror_trading", "en")
        }
        ContractConditions("@1AdminCondition") 
    }
    action {
        if $operation == "N" {
            DBInsert("@1keys", {"id":1, "ecosystem":1})
        }
        if $operation == "C" {
            var m map
            m["name"] = $name
            m["pricetick"] = $pricetick
            m["range"] = $range
            m["lotsize"] = $lotsize
            m["ecosystem_hub"] = $ecosystem
            m["tradeable"] = $isTradeable
            m["cross"] = $isCross
            m["otc"] = $isOTC
            m["wallet_hub"] = exWallet($ecosystem)
            m["wallet_apla"] = 1
            DBInsert("@1symbol", m)
        }
        if $operation == "E" {
            @1ExCancel("symbol", $name)
            var m map
            m["pricetick"] = $pricetick
            m["range"] = $range
            m["lotsize"] = $lotsize
            m["ecosystem_hub"] = $ecosystem
            DBUpdate("@1symbol", $id, m)
        }
        if $operation == "U" {
            var m map
            if $status == "TR" {
                //@1ExCancel("symbol", $name)
                m["tradeable"] = $isTradeable
            } 
            if $status == "CR" {
                m["cross"] = $isCross
            }
            if $status == "OTC" {
                m["otc"] = $isOTC
            }
            DBUpdate("@1symbol", $id, m)
        }
    }
}

contract ExOrder {
    data {
        client int
        flag string
        symbol string
        quantity int
        price money
    }
    func calcFaceValue (quantity int) money {
        var lot_size face_value money
        lot_size = Money(DBFind("@1symbol").Where({"name":$symbol}).Columns("lotsize").One("lotsize"))
        var i int
        i = 1
        face_value = Money(0)
        while i < $quantity+1 {
            face_value = face_value + lot_size
            if i == $quantity {
                return face_value
            }
            i = i + 1
        }  
    }
    func calcAplaValue (price money, quantity int) money {
        var apla_value money
        var j int
        j = 1
        apla_value = Money(0)
        while j < $quantity+1 {
            apla_value = apla_value + price
            if j == $quantity {
                return apla_value
            }
            j = j + 1
        }  
    }
    conditions {
        var tradestatus int
        tradestatus = Int(DBFind("@1symbol").Where({"name":$symbol}).Columns("tradeable").One("tradeable"))
        if (tradestatus != 1) {
           warning LangRes("@1ex_error_tradestatus", "en")
        }
        var check_eco_id ecosystem_hub int
        ecosystem_hub = Int(DBFind("@1symbol").Where({"name":$symbol}).Columns("ecosystem_hub").One("ecosystem_hub"))
        check_eco_id = Int(DBFind("@1keys").Where({"id":$client, "ecosystem":ecosystem_hub}).One("id"))
        if check_eco_id != $client {
            warning LangRes("@1ex_error_account", "en")
        }
        var balance value money
        balance = Money(DBFind("@1keys").Where({"id":$client, "ecosystem":$ecosystem_id}).Columns("amount").One("amount"))
        if $flag == "B" {
            var check_price int
            check_price = Int(DBFind("@1order_log").Where({"symbol":$symbol, "status":"A","flag":"S", "price":{"$lt":$price}}).One("price"))
            if check_price > 0 {
                warning LangRes("@1ex_error_market_orders", "en")
            }
            var face_value money
            face_value = calcFaceValue($quantity)
            if (face_value > balance) { 
                warning LangRes("@1ex_error_balance_check", "en")
            }
        }
        if $flag == "S" {
            var check_price int
            check_price = Int(DBFind("@1order_log").Where({"symbol":$symbol, "status":"A","flag":"S", "price":{"$gt":$price}}).One("price"))
            if check_price > 0 {
                warning LangRes("@1ex_error_market_orders", "en")
            }
            var apla_value money
            apla_value = calcAplaValue($price, $quantity)
            if (apla_value > balance) {  
                warning LangRes("@1ex_error_balance_check", "en")
            }
        }
    }
    action {
        var face_value apla_value money
        face_value = calcFaceValue($quantity)
        apla_value = calcAplaValue($price, $quantity)
        var ecosystem_hub wallet_hub wallet_apla int
        ecosystem_hub = Int(DBFind("@1symbol").Where({"name":$symbol}).Columns("ecosystem_hub").One("ecosystem_hub"))
        wallet_hub = Int(DBFind("@1symbol").Where({"name":$symbol}).Columns("wallet_hub").One("wallet_hub"))
        wallet_apla = Int(DBFind("@1symbol").Where({"name":$symbol}).Columns("wallet_apla").One("wallet_apla"))
        var m map
        m["flag"] = $flag
        m["client"] = $client
        m["venue"] = "EX"
        m["symbol"] = $symbol
        m["quantity"] = $quantity
        m["price"] = $price
        m["client_value"] = apla_value
        m["counterparty_value"] = face_value
        m["status"] = "A"
        m["lastmodified"] = $time
        DBInsert("@1order_log", m)
        if $flag == "S" {
            @1TokensTransfer("Amount,SenderId,RecipientId,Comment,Ecosystem", face_value, $client, wallet_hub, "EXOrder", ecosystem_hub)
        }
        if $flag == "B" {
            @1TokensTransfer("Amount,SenderId,RecipientId,Comment,Ecosystem", apla_value, $client, wallet_apla, "EXOrder", 1)
        }
    }
}

