        lot_size = Money(DBFind("@1symbol").Where({"id":$ticker_id}).Columns("lotsize").One("lotsize"))
        var i int
        i = 1
        face_value = Money(0)
        while i < $quantity+1 {
            face_value = face_value + lot_size
            if i == $quantity {
                return face_value
            }
            i = i + 1
        }  
    }
    func calcAplaValue (price money, quantity int) money {
        var apla_value money
        var j int
        j = 1
        apla_value = Money(0)
        while j < $quantity+1 {
            apla_value = apla_value + price
            if j == $quantity {
                return apla_value
            }
            j = j + 1
        }  
    }
    func checkBalance (client int, ecosystem int, value money) {
        var balance money
        balance = Money(DBFind("@1keys").Where({"id":client, "ecosystem":ecosystem}).Columns("amount").One("amount"))
        if value > balance {
            warning LangRes("@1ex_error_balance_check", "en")
        }
    }
    conditions {
        var tradestatus int
        tradestatus = Int(DBFind("@1symbol").Where({"id":$ticker_id}).Columns("otc").One("otc"))
        if (tradestatus != 1) {
           warning LangRes("@1ex_error_otcstatus", "en")
        }
        if $flag == "S" {
            var ecosystem_hub int
            ecosystem_hub = Int(DBFind("@1symbol").Where({"id":$ticker_id}).Columns("ecosystem_hub").One("ecosystem_hub"))
            var face_value money
            face_value = calcFaceValue($quantity)
            checkBalance($client, ecosystem_hub, face_value)
        }
        if $flag == "B" {
            var apla_value money
            apla_value = calcAplaValue($price, $quantity)
            checkBalance($client, 1, apla_value)
        }
        if $cp != "" {
            $recipient_id = AddressToId($cp)
            if $recipient_id == 0 {
                error Sprintf(LangRes("@1recipient_x_invalid", "en"), $cp)
            }
        }
    }
    action {
        if $cp == "" {
            $recipient_id = $counterparty
        }
        var face_value apla_value money
        face_value = calcFaceValue($quantity)
        apla_value = calcAplaValue($price, $quantity)
        var symbol string
        symbol = DBFind("@1symbol").Where({"id":$ticker_id}).Columns("name").One("name")
        var lotsize money
        lotsize = Money(DBFind("@1symbol").Where({"id":$ticker_id}).Columns("lotsize").One("lotsize"))
        var ecosystem_hub int
        ecosystem_hub = Int(DBFind("@1symbol").Where({"id":$ticker_id}).Columns("ecosystem_hub").One("ecosystem_hub"))
        var m map
        m["flag"] = $flag
        m["client"] = $client
        m["counterparty"] = $recipient_id
        m["venue"] = "EXOTC"
        m["symbol"] = symbol
        m["quantity"] = $quantity
        m["price"] = $price
        m["client_value"] = apla_value
        m["counterparty_value"] = face_value
        m["status"] = "A"
        m["lotsize"] = lotsize
        m["ecosystem_hub"] = ecosystem_hub
        @1NotificationsSend("member_id,sender,icon_name,text_header,text_body,page_name,params_map,eco_id", $recipient_id, 1, "fa fa-exchange", Sprintf("%s %s", $flag,symbol), LangRes("@1ex_new_otc", "en"), "@1ex_otc_new", m, $ecosystem)
    }
}

contract ExMatchingOTC {
    data {
        notific_id int
        status string
    }
    func checkBalance (client int, ecosystem int, value money) {
        var balance money
        balance = Money(DBFind("@1keys").Where({"id":client, "ecosystem":ecosystem}).Columns("amount").One("amount"))
        if value > balance {
            warning LangRes("@1ex_error_balance_check", "en")
        }
    }
    conditions {
        if $status == "F" {
            var m order map
            m = DBFind("@1notifications").Where({"id":$notific_id}).Columns("page_params->flag,page_params->client,page_params->counterparty,page_params->client_value,page_params->counterparty_value,page_params->price,page_params->quantity,page_params->symbol,page_params->ecosystem_hub").Row()
            if m["page_params.flag"] == "B" {
                checkBalance(Int(m["page_params.counterparty"]), Int(m["page_params.ecosystem_hub"]), Money(m["page_params.counterparty_value"]))
                checkBalance(Int(m["page_params.client"]), 1, Money(m["page_params.client_value"]))
            }
            if m["page_params.flag"] == "S" {
                checkBalance(Int(m["page_params.client"]), Int(m["page_params.ecosystem_hub"]), Money(m["page_params.counterparty_value"]))
                checkBalance(Int(m["page_params.counterparty"]), 1, Money(m["page_params.client_value"]))
            }
        }
    }
    action {
        var m order map
        m = DBFind("@1notifications").Where({"id":$notific_id}).Columns("page_params->flag,page_params->client,page_params->counterparty,page_params->client_value,page_params->counterparty_value,page_params->price,page_params->quantity,page_params->symbol,page_params->ecosystem_hub").Row()
        order["flag"] = Str(m["page_params.flag"])
        order["symbol"] = Str(m["page_params.symbol"])
        order["client"] = Int(m["page_params.client"])
        order["counterparty"] = Int(m["page_params.counterparty"])
        order["client_value"] = Money(m["page_params.client_value"])
        order["counterparty_value"] = Money(m["page_params.counterparty_value"])
        order["quantity"] = Int(m["page_params.quantity"])
        order["price"] = Money(m["page_params.price"])
        order["venue"] = "EXOTC"
        order["status"] = $status
        if $status == "F" {
            if order["flag"] == "B" {
                @1TokensTransfer("Amount,SenderId,RecipientId,Comment,Ecosystem", order["counterparty_value"], order["counterparty"], order["client"], "ExMatchingOTC", m["page_params.ecosystem_hub"])
                @1TokensTransfer("Amount,SenderId,RecipientId,Comment,Ecosystem", order["client_value"], order["client"], order["counterparty"], "ExMatchingOTC", 1)
            }
            if order["flag"] == "S" {
                @1TokensTransfer("Amount,SenderId,RecipientId,Comment,Ecosystem", order["counterparty_value"], order["client"], order["counterparty"], "ExMatchingOTC", m["page_params.ecosystem_hub"])
                @1TokensTransfer("Amount,SenderId,RecipientId,Comment,Ecosystem", order["client_value"], order["counterparty"], order["client"], "ExMatchingOTC", 1)
            }

        }
        DBInsert("@1order_log", order)
        @1NotificationsClose("notific_id", $notific_id)
    }
}

contract ExCancelOrders {
    data {
        symbol string
        client int
        flag string
    }
    conditions {
    }
    action {
        var ids array
        var i ecosystem_hub wallet_apla wallet_hub int
        ecosystem_hub = Int(DBFind("@1symbol").Where({"name":$symbol}).Columns("ecosystem_hub").One("ecosystem_hub"))
        wallet_hub = Int(DBFind("@1symbol").Where({"name":$symbol}).Columns("wallet_hub").One("wallet_hub"))
        wallet_apla = Int(DBFind("@1symbol").Where({"name":$symbol}).Columns("wallet_apla").One("wallet_apla"))
        i = 0
        ids = DBFind("@1order_log").Where({"symbol":$symbol, "status":"A", "client":$client, "flag":$flag}).Columns("id,client_value,counterparty_value")
        while i < Len(ids) {
            var param map
            param = ids[i]
            var client id int
            client = Int(param["client"])
            id = Int(param["id"])
            var amount money
            if $flag == "S" {
                amount = Money(param["counterparty_value"])
                @1TokensTransfer("Amount,SenderId,RecipientId,Comment,Ecosystem", amount, wallet_hub, $client, "ExCancel", ecosystem_hub)
            }
            if $flag == "B" {
                amount = Money(param["client_value"])
                @1TokensTransfer("Amount,SenderId,RecipientId,Comment,Ecosystem", amount, wallet_apla, $client, "ExCancel", 1)
            }
            DBUpdate("@1order_log", id, {status:"C", lastmodified:$time})
            i = i + 1
        }  

    }
}
