Div(fullscreen){
    Div(table-responsive ml-lg mr-lg){
        Div(list-group-item){
            If(#count# > 0){
                Table(src, "$@1template$=title,$@1participants$=_participants,$@1decision$=_decision,=_actions")
            }.Else{
                Div(Class: text-center h4 text-muted, Body: "$@1polls_templates$ $@1not_founded$")
            }
        }.Style(
            margin-top:-15px;
