contract AppRolesSettings{
    data {
        RolesParam string
        AppIds array
        Ids array
        AppId int
    }

    conditions{
        ContractConditions("AdminCondition")
    }
    func trimPrefix(s string) string{
        var d array  i int canBreak bool
        d = ["@", "1", "2", "3", "4", "5", "6", "7", "8", "9", "0"]

        while true{
            canBreak = true
            while i < 11{
                if HasPrefix(s, d[i]){
                    s = Substr(s, 1, Size(s))
                    canBreak = false
                }
                i = i + 1
            }
            if canBreak {
                break
            }
        }
        return s
    }

    action {
        var i i_param lenIds paramId int rolesParam array
        lenIds = Len($Ids)
        rolesParam = Split(AppParam($AppId, $RolesParam, $ecosystem_id), ",")
        while i < lenIds{
            var name string roleId int
            i_param = Int($AppIds[i])
            name = trimPrefix(rolesParam[i_param - 1])
            roleId = $Ids[i]
            if roleId > 0{
                paramId = Int(DBFind("@1app_params").Where({app_id:$AppId, ecosystem:$ecosystem_id, name:{$end:name}}).One("id"))
                @1EditAppParam("Id,Value", paramId, roleId)
            }
            i = i + 1
        }
    }
}

contract CFAdmin {
    data {
        Id int
        Action string
    }
    conditions{
        $startup = DBFind("@1startups").WhereId($Id).Row()
        if !$startup{
            warning "Startup not found"
        }
        // TODO: проверка прав на администрирование (role_id == cf_catalog_admin)
        $CREATED = 1
        $PUBLISHED = 2
        $STARTED = 3
        $FINISHED = 4
        $FAILED = 5
        $STOPPED = 6
        $REJECTED = 7
    }

    action {
        if $Action == "stop"{
            $startup["stopped_at"] = $block_time
            $startup["status"] = $STOPPED
        }elif $Action == "accept"{
            $startup["published_at"] = $block_time
            $startup["status"] = $PUBLISHED
        }elif $Action == "reject"{
            $startup["published_at"] = -1
            $startup["status"] = $REJECTED
        }elif $Action == "delete"{
            $startup["deleted_at"] = $block_time
            $startup["status"] = $STOPPED
        }
        DBUpdate("@1startups", $Id, $startup)
    }
}

