data.Value:
SetVar(this_page,"@1errand_create")

If(#EcosystemId# == #ecosystem_id#){
    SetVar(errand_type,1)
}
If(#errand_type#==""){
    SetVar(errand_type,0)
}
If(#MemberId# == ""){
    SetVar(MemberId, 0)
}

Form(){
