                    }.Else{
                        Input(Name: m_name, Disabled: 1, Value: "xxxx-xxxx-xxxx-xxxx-xxxx")
                    }
                    Div(input-group-btn){
                        Button(Class: btn bg-gray-lighter fa fa-caret-down buttons, Page: @1select_wallet, PageParams:"back_page=#this_page#,back_header=$@1send$,EcosystemId=#ecosystem_id#").Popup(Header: $@1member$, Width: "50")
                    }.Style(
                        .buttons{border: 1px solid #dde6e9;}
                    )
                }
            }
        }
    }
    If(And(#MemberId# != 0, #errand_type# == 1)){
        Div(row mt-sm){
            Div(col-md-3 mt-sm text-right){
                Label(){
                    $@1name$
                    Span(*,text-danger)
                }
            }
            Div(col-md-9 text-left){
                Input(Name: ErrandName, Value: $@1new_errand$)
            }
        }
        Div(row mt-sm){
            Div(col-md-3 mt-sm text-right){
                Label(){
                    $@1date_end$
                    Span(*,text-danger)
                }
            }
            Div(col-md-9 text-left){
                Div(row){
                    Div(col-md-6){
                        Input(Name: EndDate, Type: date)
                    }
                    Div(col-md-6){
                        Input(Name: EndTime, Type: time, Value: "00:00")
                    }
                }
            }
        }
        Div(row mt-sm){
            Div(col-md-3 mt text-right){
                Label(){
                    $@1body_text$
                    Span(*,text-danger)
                }
            }
            Div(col-md-9 text-left){
                Input(Name: ErrandText, Type: textarea)
            }
        }
    }.ElseIf(#errand_type#==0){
        If(#role_type_errand# != 2){
            SetVar(role_type_errand,1)
        }
        Div(row mt-sm){
            Div(col-md-3 mt-sm text-right){
                Label(){
                    $@1type_role_errand$
                    Span(*,text-danger)
                }
            }
            Div(col-md-9 mt-sm text-left){
                If(And(#errand_type#==0,#role_type_errand# == 1)){
                    Button(Body: $@1one_role_representative$, Class: btn-xs btn-primary mr-sm disabled)
                    Button(Body: $@1all_roles_member$, Page: #this_page#, PageParams: "errand_type=0,role_type_errand=2", Class: btn-xs btn-default).Popup(50, "$@1new_errand$")
                }.ElseIf(And(#errand_type#==0,#role_type_errand# == 2)){
                    Button(Body: $@1one_role_representative$, Page: #this_page#, PageParams: "errand_type=0,role_type_errand=1", Class: btn-xs btn-default mr-sm).Popup(50, "$@1new_errand$")
                    Button(Body: $@1all_roles_member$, Class: btn-xs btn-primary disabled)
                }
            }
        }
        Div(row mt-sm){
            Div(col-md-3 mt-sm text-right){
                Label(){
                    $@1name$
                    Span(*,text-danger)
                }
            }
            Div(col-md-9 text-left){
                Input(Name: ErrandName, Value: $@1new_errand$)
            }
        }
        Div(row mt-sm){
            Div(col-md-3 mt-sm text-right){
                Label(){
                    $@1date_end$
                    Span(*,text-danger)
                }
            }
            Div(col-md-9 text-left){
                Div(row){
                    Div(col-md-6){
                        Input(Name: EndDate, Type: date)
                    }
                    Div(col-md-6){
                        Input(Name: EndTime, Type: time, Value: "00:00")
                    }
                }
            }
        }
        Div(row mt-sm){
            Div(col-md-3 mt text-right){
                Label(){
                    $@1body_text$
                    Span(*,text-danger)
                }
            }
            Div(col-md-9 text-left){
                Input(Name: ErrandText, Type: textarea)
            }
        }
    }
    Div(row mt){
        Div(col-md-12){
            Input(Name: SendRoleId, Class: hidden, Value: #role_id#)
            Button(Body: $@1send$, Class: btn btn-primary pull-right, PageParams:"type=#type#", Page: @1errand_list, Contract: @1ErrandCreate, Params: "ErrandType=#errand_type#,RoleTypeErrand=#role_type_errand#")
            Button(Body: $@1back$, Class: btn btn-default pull-right, PageParams:"type=#type#", Page: @1errand_list)
        }
    }
}

SetVar(this_page, @1errand_list).(this_table, @1notifications)
Include(@1pager_header)

If(#page_par#!=""){
    SetVar(Name: type, Value: #page_par#)
}
If(And(#type#!=0,#type#!=1)){
    SetVar(Name: type, Value: 0)
}

SetTitle($@1errands$)
DBFind("@1applications").Where({"ecosystem": #ecosystem_id#, "name": "Errands"}).Vars(application)
SetVar(admin_roles, AppParam(Ecosystem: #ecosystem_id#, App: #application_id#, Name: errands_admin_role))
DBFind("@1roles_participants").Where({"ecosystem": #ecosystem_id#, "role->id": {"$in": [#admin_roles#]}, "member->member_id": #key_id#, "deleted": 0}).Vars(admin_access)
SetVar(create_roles, AppParam(Ecosystem: #ecosystem_id#, App: #application_id#, Name: errands_access))
DBFind("@1roles_participants").Where({"ecosystem": #ecosystem_id#, "$and": ["role->id": {"$in": [#create_roles#]}, "role->id": #role_id#], "member->member_id": #key_id#, "deleted": 0}).Vars(create_access)
If(#create_access_id# > 0){
    AddToolButton(Title: $@1new_errand$, Page: @1errand_create, PageParams:"type=#type#", Icon: icon-plus).Popup(50, "$@1new_errand$")
}

Div(btn-group){
    Div(btn-group ml-lg){
        If(#type#==0){
            Button(Body: Em(Class: fa fa-user) $@1individual_errands$, Page: @1errand_list, PageParams: "type=0", Class: btn bg-gray-lighter ml-sm)
        }.Else{
            Button(Body: Em(Class: fa fa-user) $@1individual_errands$, Page: @1errand_list, PageParams: "type=0", Class: btn bg-gray ml-sm)
        }
        If(#type#==1){
            Button(Body: Em(Class: fa fa-users) $@1roles_errands$, Page: @1errand_list, PageParams: "type=1", Class: btn bg-gray-lighter ml-sm)
        }.Else{
            Button(Body: Em(Class: fa fa-users) $@1roles_errands$, Page: @1errand_list, PageParams: "type=1", Class: btn bg-gray ml-sm)
        }
        If(#admin_access_id#>0){
            Button(Body: Em(Class: fa fa-refresh) $@1update_statuses$, Class: btn btn-default ml-lg, Page: @1errand_list, PageParams: "type=#type#", Contract: @1ErrandStatusUpdate)
        }
    }
    Div(btn-group mr-lg pull-right){
        If(#search#){
            Button(Class: btn bg-gray-lighter, Page: @1errand_list, PageParams: "Status=0,search=#search#,type=#type#"){Span(Body: Em(Class: fa fa-hourglass-half), Class: text-primary)}
            Button(Class: btn bg-gray-lighter, Page: @1errand_list, PageParams: "Status=1,search=#search#,type=#type#"){Span(Body: Em(Class: fa fa-hourglass-end), Class: text-success)}
            Button(Class: btn bg-gray-lighter, Page: @1errand_list, PageParams: "Status=-1,search=#search#,type=#type#"){Span(Body: Em(Class: fa fa-times), Class: text-danger)}
        }.Else{
            Button(Class: btn bg-gray-lighter, Page: @1errand_list, PageParams: "Status=0,type=#type#"){Span(Body: Em(Class: fa fa-hourglass-half), Class: text-primary)}
            Button(Class: btn bg-gray-lighter, Page: @1errand_list, PageParams: "Status=1,type=#type#"){Span(Body: Em(Class: fa fa-hourglass-end), Class: text-success)}
            Button(Class: btn bg-gray-lighter, Page: @1errand_list, PageParams: "Status=-1,type=#type#"){Span(Body: Em(Class: fa fa-times), Class: text-danger)}
        }
    }
    If(Or(#search#!="",#Status#==-1,#Status#==0,#Status#==1)){
        Span(Button(Class: btn btn-link pull-right, Page: @1errand_list,PageParams: "type=#type#", Body: $@1clear_filters$)).Style(padding-left:10px)
    }
}

If(#type#==1){
    SetVar(where_recipient, {"recipient->role_id": {"$neq": 0}})
}.Else{
    SetVar(where_recipient, {"recipient->member_id": {"$neq": 0}})
}

If(Or(#Status#==-1,#Status#==0,#Status#==1)){
    SetVar(where_status, {"page_params->status": #Status#})
}

If(#search#){
    SetVar(where_search, {"page_params->name": {"$ilike": "#search#"}})
}

SetVar(where, {"$and": [{"ecosystem": #ecosystem_id#}, {"page_params->type": "task"}, #where_recipient#, #where_status#, #where_search#]})

Div(list-group-item ml-lg mr-lg pt-lg){
    SetVar(search_name, LangRes(@1name)).(page_par, #type#)
    Include(@1search)
}

DBFind(#this_table#, src).Where(#where#).Order({"id": -1}).Limit(#pager_limit#).Offset(#pager_offset#).Columns("id,ecosystem,page_params->status,page_params->name,recipient->role_name,recipient->role_id,recipient->member_name,recipient->member_id,recipient->image_id,sender->image_id,sender->role_name,sender->role_id,date_created,page_params->date_end").Custom(_status){
    If(#page_params.status# == 0){
        Span(Body:Em(Class:fa fa-hourglass-half) $@1in_progress$,Class: text-primary h5)
    }.ElseIf(#page_params.status# == -1){
        Span(Body:Em(Class:fa fa-times) $@1expired$,Class: text-danger h5)
    }.ElseIf(#page_params.status# == 1){
        Span(Body:Em(Class:fa fa-hourglass-end) $@1done$,Class: text-success h5)
    }
}.Custom(_role){
    If(#recipient.role_id# > 0){
        LinkPage(Class: text-primary h5 text-bold, Page: @1roles_view, PageParams: "v_role_id=#recipient.role_id#"){
            Div(){
                Span(Class: icon-people fa-2x mr-sm).(#recipient.role_name#)
            }.Style(display:flex; align-items:center;)
        }
    }.ElseIf(#recipient.member_id# != 0){
        LinkPage(Class: text-primary h5 text-bold, Page: @1profile_view, PageParams: "v_key_id=#recipient.member_id#"){
            If(#recipient.image_id#>0){
                Image(Src: Binary().ById(#recipient.image_id#), Class: img-circle).Style(height: 30px;width: 30px; border: 1px solid #5A5D63; margin-right: 10px;)
                Span(#recipient.member_name#)
            }.Else{
                Div(){
                    Span(Em(Class: fa icon-user fa-2x)).Style(margin-right:10px;)
                    Span(#recipient.member_name#)
                }.Style(display:flex; align-items:center;)
            }
        }
    }
}.Custom(_name){
    LinkPage(#page_params.name#, Class: text-primary h5 text-bold,Page: @1errand_view,PageParams: "errand_id=#id#,type=#type#")
}.Custom(custom_arrow){
    Em(Class: fa fa-long-arrow-right fa-1x)
}.Custom(_sender){
    LinkPage(Class: text-primary h5 text-bold, Page: @1roles_view, PageParams: "v_role_id=#sender.role_id#"){
        If(#sender.image_id#>0){
            Image(Src: Binary().ById(#sender.image_id#), Class: mr-sm).Style(width: 30px; border: 1px solid #5A5D63;)
            #sender.role_name#
        }.Else{
            Div(){
                Span(Class: icon-people fa-2x mr-sm).(#sender.role_name#)
            }.Style(display:flex; align-items:center;)
        }
    }
}.Custom(_date_start){
    Div(Class:h5){DateTime(DateTime: #date_created#, Format: "YYYY.MM.DD HH:MI:SS")}
}.Custom(_date_end){
    Div(Class:h5){Span(#page_params.date_end#)}
}.Custom(_del){
    If(#admin_access_id#>0){
        Button(Body: Em(Class: text-danger fa fa-trash), Class: btn btn-default pull-right, Contract: @1ErrandDelete, Page: @1errand_list, Params: "ErrandId=#id#", PageParams: "type=#type#")
    }
}.Count(count)

Div(fullscreen){
    Div(table-responsive ml-lg mr-lg){
        Div(list-group-item){
            If(#count# > 0){
                Table(src, "$@1name$=_name,$@1sender$=_sender,=custom_arrow,$@1recipient$=_role,$@1date_start$=_date_start,$@1date_end$=_date_end,$@1status$=_status,=_del")
            }.Else{
                Div(Class: text-center h4 text-muted, Body: "$@1errands$ $@1not_founded$")
            }
        }.Style(
            margin-top:-15px;
            tbody > tr:nth-of-type(odd) {
                background-color: #f8f9fc;
            }
        )
    }
}
Div(mt-sm ml-lg mr-sm mb-sm){
    Include(@1pager)
}

Div(content-wrapper){
    If(#notific_id#>0){
        DBFind(@1notifications).WhereId(#notific_id#).Columns("id,page_params->decision_errand_id,page_params->type,processing_info,closed").Vars(check_decisions)
        If(And(#check_decisions_page_params_type# == task_decision,#check_decisions_page_params_decision_errand_id# != "")){
            SetVar(errand_id,#check_decisions_page_params_decision_errand_id#)
        }.Else{
            SetVar(errand_id,#notific_id#)
        }
    }.ElseIf(#errand_id#>0){
        SetVar(errand_id,#errand_id#)
    }

    DBFind(@1notifications).Where({"ecosystem": #ecosystem_id#, "id": #errand_id#}).Columns("id,page_params->name,sender->role_id,sender->role_name,sender->image_id,recipient->role_id,recipient->role_name,recipient->image_id,recipient->member_id,recipient->member_name,page_params->status,page_params->date_end,date_created,page_params->text,page_params->answer,processing_info,processing_info->member_id,processing_info->member_name,processing_info->image_id,closed").Vars(prefix)

    Form(row){
        Div(col-md-6 col-md-offset-3){
            Div(list-group-item text-center){
                Span(Class: text-bold h4, Body: #prefix_page_params_name#)
            }
            Div(list-group-item){
                Div(row){
                    Div(col-md-5 mt-sm text-right){
                        Button(Class: btn-link text-primary h5 text-bold, Page: @1roles_view, PageParams: "v_role_id=#prefix_sender_role_id#"){
                            If(#prefix_sender_image_id#>0){
                                Image(Src: Binary().ById(#sender.image_id#), Class: mr-sm).Style(width: 30px; border: 1px solid #5A5D63;)
                                #prefix_sender_role_name#
                            }.Else{
                                Div(){
                                    Span(Class: icon-people fa-2x mr-sm).(#prefix_sender_role_name#).Style(word-break: break-all;)
                                }.Style(display:flex; align-items:center;)
                            }
                        }
                    }
                    Div(col-md-2 mt-sm text-center){
                        Em(Class: fa fa-long-arrow-right text-muted fa-2x)
                    }.Style(padding-top:12px;)
                    Div(col-md-5 mt-sm text-left){
                        If(#prefix_recipient_role_id# > 0){
                            Button(Class: btn-link text-primary h5 text-bold, Page: @1roles_view, PageParams: "v_role_id=#prefix_recipient_role_id#"){
                                If(#prefix_recipient_image_id#>0){
                                    Image(Src: Binary().ById(#sender.image_id#), Class: mr-sm).Style(width: 30px; border: 1px solid #5A5D63;)
                                    Span(#prefix_recipient_role_name#).Style(word-break: break-all;)
                                }.Else{
                                    Div(){
                                        Span(Class: icon-people fa-2x mr-sm).(#prefix_recipient_role_name#).Style(word-break: break-all;)
                                    }.Style(display:flex; align-items:center;)
                                }
                            }
                        }.ElseIf(#prefix_recipient_member_id# != 0){
                            Button(Class: btn-link text-primary h5 text-bold, Page: @1profile_view, PageParams: "v_key_id=#prefix_recipient_member_id#"){
                                If(#prefix_recipient_image_id#>0){
                                    Image(Src: Binary().ById(#prefix_recipient_image_id#), Class: img-circle).Style(height: 30px;width: 30px; border: 1px solid #5A5D63; margin-right: 10px;)
                                    Span(#prefix_recipient_member_name#)
                                }.Else{
                                    Div(){
                                        Span(Em(Class: fa icon-user fa-2x)).Style(margin-right:10px;)
                                        Span(#prefix_recipient_member_name#).Style(word-break: break-all;)
                                    }.Style(display:flex; align-items:center;)
                                }
                            }
                        }
                    }
                }
                Div(row){
                    Div(col-md-5 mt-sm text-right){
                        Span(Class: h5, Body: $@1status$)
                    }
                    Div(col-md-2 text-center)
                    Div(col-md-5 mt-sm text-left){
                        If(#prefix_page_params_status# == 0){
                            Span(Body:Em(Class:fa fa-hourglass-half) $@1in_progress$, Class: text-primary h5)
                        }.ElseIf(#prefix_page_params_status# == -1){
                            Span(Body:Em(Class:fa fa-times) $@1expired$, Class: text-danger h5)
                        }.ElseIf(#prefix_page_params_status# == 1){
                            Span(Body:Em(Class:fa fa-hourglass-end) $@1done$, Class: text-success h5)
                        }
                    }
                }
                Div(row){
                    Div(col-md-5 mt-sm text-right){
                        Span(Class: h5, Body: $@1date_start$)
                    }
                    Div(col-md-2 mt-sm text-center)
                    Div(col-md-5 mt-sm text-left){
                        Span(Class: h5){DateTime(DateTime: #prefix_date_created#, Format: "YYYY.MM.DD HH:MI:SS")}
                    }
                }
                Div(row){
                    Div(col-md-5 mt-sm text-right){
                        Span(Class: h5, Body: $@1date_end$)
                    }
                    Div(col-md-2 mt-sm text-center)
                    Div(col-md-5 mt-sm text-left){
                        Span(#prefix_page_params_date_end#,Class: h5)
                    }
                }
                If(And(#prefix_processing_info# != "",#prefix_recipient_role_id# > 0)){
                    Div(row){
                        Div(col-md-5 mt-sm text-right){
                            Span(Class: h5, Body: $@1performer_from_role$)
                        }
                        Div(col-md-2 mt-sm text-center)
                        Div(col-md-5 mt-sm text-left){
                            LinkPage(Class: text-primary h5 text-bold, Page: @1profile_view, PageParams: "v_key_id=#prefix_processing_info_member_id#"){
                                Span(#prefix_processing_info_member_name#)
                            }
                        }
                    }
                }
            }
            Div(list-group-item){
                Div(row){
                    Div(col-md-12){
                        Span(Class: h5 text-bold, Body: $@1errand_text$)
                        Div(list-group-item){
                            Span(Class: h5, Body: #prefix_page_params_text#)
                        }
                    }
                }
                If(Or(#prefix_recipient_member_id# == #key_id#,#prefix_processing_info_member_id# == #key_id#)){
                    Div(row){
                        Div(col-md-12){
                            Span(Class: h5 text-bold, Body: $@1answer$)
