                        If(#cl_closed# == 0){
                            If(#cl_processing_info# == ""){
                                Button($@1start_process$,Class: btn btn-primary, Page: @1errand_view, PageParams: "notific_id=#notific_id#", Contract: @1NotificationsProcess, Params: "notific_id=#notific_id#")
                            }.Else{
                                Button($@1close$, Class: btn btn-default, Page: @1notifications_list, Contract: @1NotificationsClose,Params: "notific_id=#notific_id#")
                            }
                        }.Else{
                            Button($@1back$, Class: btn btn-default, Page: @1errand_list, PageParams: "type=#type#")
                        }
                    }
                }.Else{
                    If(#check_decisions_closed# == 0){
                        If(#check_decisions_processing_info# == ""){
                            Button($@1start_process$, Class: btn btn-primary, Page: @1errand_view, PageParams: "notific_id=#notific_id#", Contract: @1NotificationsProcess, Params: "notific_id=#notific_id#")
                        }.Else{
                            Button($@1close$, Class: btn btn-default, Page: @1notifications_list, Contract: @1NotificationsClose, Params: "notific_id=#notific_id#")
                        }
                    }.Else{
                        If(#notific_id# > 0){
                            Button($@1back$, Class: btn btn-default, Page: @1notifications_list, PageParams: "type=#type#")
                        }.Else{
                            Button($@1back$, Class: btn btn-default, Page: @1errand_list, PageParams: "type=#type#")
                        }
                    }
                }
            }
        }
    }
}

contract ErrandCreate {
    data {
        ErrandType int
        RecRoleId int "optional"
        SendRoleId int
        MemberId string "optional"
        ErrandName string
        ErrandText string
        RoleTypeErrand int "optional"

        EndDate string
        EndTime string "optional"
    }

    func isCalledFromPage() bool {
        return $stack[0] == Sprintf("@%v%v", $ecosystem_id, $this_contract)
    }

    func trimZeroTime(s string) string {
        if Contains(s, "T00:00:00Z") {
            s = s Replace(s, "T00:00:00Z", "")
        }
        return s
    }

    func dateAddTime(d, t string) string {
        var dt string
        if Size(t) == 5 {
            dt = Sprintf("%v %v:00", trimZeroTime(d), t)
        }
        return dt
    }

    func fixDatetimes() {
        $date_ended = dateAddTime($EndDate, $EndTime)

        if UnixDateTime($date_ended) == 0 { // invalid datetimes
            if isCalledFromPage(){
                var errs array
                if Size($EndDate) < 10 {
                    error LangRes("@1ending_date_invalid", "en")
                }
                if Size($EndTime) < 5 {
                    error LangRes("@1invalid_time_end", "en")
                }
            }
        }
        if UnixDateTime($date_ended) < $block_time{
            error LangRes("@1starting_date_invalid", "en")
        }
    }

    conditions {
        $app = DBFind("@1applications").Where({"ecosystem": $ecosystem_id, "name": "Errands"}).One("id")
        $check_access = AppParam(Int($app), "errands_access", $ecosystem_id)
        if $check_access {
            var rids array
            rids = JSONDecode("["+$check_access+"]")
            if !DBFind("@1roles_participants").Where({"ecosystem": $ecosystem_id, "role->id": {"$in": rids}, "member->member_id": $key_id, "deleted": 0}).Row() {
                warning LangRes("@1access_denied", "en")
            }
        } else {
            var admin_ids int
            admin_ids = AppParam(Int($app), "errands_admin_role", $ecosystem_id)
            var rids array
            rids = JSONDecode("["+admin_ids+"]")
            if !DBFind("@1roles_participants").Where({"ecosystem": $ecosystem_id, "role->id": {"$in": rids}, "member->member_id": $key_id, "deleted": 0}).Row() {
                warning LangRes("@1access_denied", "en")
            }
        }
        if $ErrandType == 0 {
            $valid_role = DBFind("@1roles").Where({"ecosystem": $ecosystem_id, "id": $RecRoleId, "deleted": 0}).One("id")
            if $valid_role == 0 {
                info LangRes("@1role_not_found", "en")
            }
        }
        if $ErrandType == 1 {
            if $MemberId == "" {
                info LangRes("@1empty_wallet", "en")
            }
            $MemberId = AddressToId($MemberId)
            $check_member = DBFind("@1members").Where({"id": $MemberId, "ecosystem": $ecosystem_id}).One("id")
            if (Int($check_member) == 0){
                if !DBFind("@1keys").Where({"ecosystem": $ecosystem_id, "id": $MemberId}).Columns("id").Row() {
                    error LangRes("@1member_not_found", "en")
                }
            }
            if $EndDate == "" {
                info LangRes("@1ending_date_invalid", "en")
            }
            if $EndTime == "" {
                info LangRes("@1invalid_time_end", "en")
            }
            if $ErrandName == "" {
                info LangRes("@1empty_name", "en")
            }
            if $ErrandText == "" {
                info LangRes("@1empty_text", "en")
            }
            }
        fixDatetimes()
    }

    action {
        var info_errand map

        info_errand["name"] = $ErrandName
        info_errand["text"] = $ErrandText
        info_errand["date_end"] = $date_ended
        info_errand["status"] = 0
        info_errand["type"] = "task"
        if $ErrandType == 0 {
            if $RoleTypeErrand == 2 {
                info_errand["recipient_type"] = 1
            } else{
                info_errand["recipient_type"] = 0
            }
        }
        if $ErrandType == 1 {
            info_errand["recipient_type"] = 1
        }

        if $ErrandType == 0 {
            if $RoleTypeErrand == 2 {
                var errands array i lenErrands member_r_id int v map
                errands = DBFind("@1roles_participants").Columns("id,role->id,member->member_id,ecosystem,deleted").Where({"role->id": $RecRoleId, "deleted": 0, "ecosystem": $ecosystem_id})
                lenErrands = Len(errands)
                if lenErrands == 0 {
                    info(LangRes("@1recipient_role_no_members", "en"))
                }
                while i < lenErrands{
                    v = errands[i]
                    member_r_id = Int(v["member.member_id"])
                    @1NotificationsSend("current_role_id,member_id,sender,icon_name,text_header,text_body,page_name,params_map,rid,closure_type,eco_id",$SendRoleId,member_r_id, 2, "icon icon-envelope-open", LangRes("@1new_errand", "en"), LangRes("@1details_view", "en"), "@1errand_view", info_errand, 0, 0,$ecosystem_id)
                    i = i + 1
                }
            } else {
                @1NotificationsSend("current_role_id,rid,sender,icon_name,text_header,text_body,page_name,params_map,closure_type,eco_id",$SendRoleId,$RecRoleId, 2, "icon icon-envelope-open", LangRes("@1new_errand", "en"), LangRes("@1details_view", "en"), "@1errand_view",info_errand,1,$ecosystem_id)
            }
        }
        if $ErrandType == 1 {
            @1NotificationsSend("current_role_id,member_id,sender,icon_name,text_header,text_body,page_name,params_map,rid,closure_type,eco_id",$SendRoleId,Int($MemberId), 2, "icon icon-envelope-open", LangRes("@1new_errand", "en"), LangRes("@1details_view", "en"), "@1errand_view", info_errand, 0, 0,$ecosystem_id)
        }
    }
}

contract ErrandDecision {
    data {
        ErrandDecision int
        ErrandId int
        AnswerRecipient string
    }

    conditions {
        if $AnswerRecipient == "" {
            info LangRes("@1empty_answer", "en")
        }
        $errand_map = DBFind("@1notifications").Where({"id": $ErrandId}).Columns("id,processing_info,processing_info->member_id,page_params,page_params->name,page_params->text,page_params->type,page_params->date_end,closed,recipient->role_id,recipient->member_id,page_params->recipient_type,sender->role_id").Row()
        if Int($errand_map["page_params.recipient_type"]) == 0 { //role errand
            if !RoleAccess(Int($errand_map["recipient.role_id"])) {
                warning LangRes("@1access_denied", "en")
            }
            if $errand_map["processing_info"] == "" {
                info LangRes("@1start_process_errand", "en")
            }
            if Int($errand_map["processing_info.member_id"]) != $key_id {
                info LangRes("@1another_start_process_errand", "en")
            }
        } else { //member errand
            if Int($errand_map["recipient.member_id"]) != $key_id {
                warning LangRes("@1access_denied", "en")
            }
        }
        if $errand_map["closed"] != 0 {
            info LangRes("@1already_start_process_errand", "en")
        }
    }

    action {
        var answer_map map
        answer_map["name"] = $errand_map["page_params.name"]
        answer_map["text"] = $errand_map["page_params.text"]
        answer_map["type"] = $errand_map["page_params.type"]
        answer_map["date_end"] = $errand_map["page_params.date_end"]
        answer_map["answer"] = $AnswerRecipient

        if $ErrandDecision == 1 {
            answer_map["status"] = 1
        } else {
            answer_map["status"] = -1
        }
        var page_params_edit map
        page_params_edit["page_params"] = answer_map

        @1NotificationsUpdateParams("page_params,notific_id",page_params_edit,$ErrandId)

        if Int($errand_map["page_params.recipient_type"]) == 0 {
            var info_errand map
            info_errand["decision_errand_id"] = $ErrandId
            info_errand["type"] = "task_decision"
            @1NotificationsSend("current_role_id,rid,sender,icon_name,text_header,text_body,page_name,params_map,closure_type,eco_id",Int($errand_map["recipient.role_id"]),Int($errand_map["sender.role_id"]), 2, "icon icon-envelope-open", "Decision by errand", LangRes("@1details_view", "en"), "@1errand_view",info_errand,1,$ecosystem_id)
        }
        else{
            var info_errand map
            info_errand["decision_errand_id"] = $ErrandId
            info_errand["type"] = "task_decision"
            @1NotificationsSend("rid,sender,icon_name,text_header,text_body,page_name,params_map,closure_type,eco_id",Int($errand_map["sender.role_id"]), 1, "icon icon-envelope-open", "Decision by errand", LangRes("@1details_view", "en"), "@1errand_view",info_errand,1,$ecosystem_id)
        }
        @1NotificationsClose("notific_id", $ErrandId)
    }
}

