        SetVar(where,"{question_id:#q_id#,answer_id:#a_id#}")
    }.Else{
        SetVar(where,"{question_id:#q_id#}")
    }

    DBFind(@1q_answers,src_a).Where(#where#).Limit(#pager_limit#).Custom(ans){
        #answer_id# : #decision#
    }.Custom(c_m){
        DBFind(@1members).Where({ecosystem:#ecosystem_id#, id:#member#}).Vars(m)
        If(Or(#m_id#>0,#m_id#<0)){
            SetVar(member_name, #m_member_name#)
        }.Else{
            SetVar(member_name, Address(#id#))
        }
        Span(#member_name#)
    }.Custom(c_ecosystem){
        DBFind(@1ecosystems).Where({id:#ecosystem#}).Vars(e)
        If(Or(#e_id#>0,#e_id#<0)){
            SetVar(eco_name, #e_name#)
        }.Else{
            SetVar(eco_name, #ecosystem#)
        }
        Span(#eco_name#)
    }.Custom(time){
        DateTime(#lastmodified#,HH:MI DD.MM.YYYY)
    }.Count(a)

    Div(row form-group){
        Div(col-sm-12){           
            Table(Source: src_a, Columns:"$@1q_answer_member$=c_m,$@1ecosystem_name$=c_ecosystem,$@1q_date_lastmodified$=time,$@1q_answer$=ans")
            Div(mt-sm ml-lg mr-sm mb-sm){
                Include(@1pager)
            }
        }
    } 
    Div(row mt){
        Div(col-md-12){
            Button(Body: LangRes(@1back), Class: btn btn-default, Page: @1q_listing)
        }
    }
}

